  return (
    <section className="Course">
      <Paper className ='page-content' flexName='flexible vertical jBetween'>
        <ReactPlayer
          className="player"
          url={url}
          controls
          autoPlay
          loop={false}
          playing
          onEnded={data => console.log('onEnded', data)}
        />
        <ul>
          {data.items.map(item => (
              <li key={item.name} onClick={() => setUrl(item.url)}>{item.url}</li>
          ))}
        </ul>
      </Paper>
    </section>
  )
};

export default Course;
