import React, { useState } from 'react';

import { Paper } from 'components';

const Course = () => {

  const data = {
    Name: 'React Native Advanced',
    Description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
    items: [
      {
        name: 'Welcome to the Course!',
        duration: '1min',
        url: 'https://vs1.coursehunter.net/udemy-complete-js-course/lesson1.mp4',
      },
      {
        name: 'Setting up our Tools',
        duration: '1min',
        url: 'https://vs1.coursehunter.net/udemy-complete-js-course/lesson2.mp4',
      },
      {
        name: 'Lets start coding',
        duration: '1min',
        url: 'https://vs1.coursehunter.net/udemy-complete-js-course/lesson3.mp4',
      },
    ]
  };

  const [url, setUrl] = useState(data.items[0].url);

  return (
    <section className="Course">
      <Paper className ='page-content' flexName='flexible jBetween'>
        <video autoplay src={url} />
        <ul>
        {data.items.map(item => (
            <li key={item.name}>{item.url}</li>
        ))}
        </ul>
      </Paper>
    </section>
  )
};

export default Course;
